const DeleteLists = React.memo( ({setTodoData}) => {

    console.log('DeleteLists rendering');

    const handleDeleteAll = () => {
        const newTodoData = [];
        setTodoData(newTodoData);
      };

      return (
        <button
          className="p-2 text-white bg-red-300 hover:bg-red-500 shadow-md rounded"
          onClick={handleDeleteAll}
        >
          전체삭제
        </button>
      );
});


export default DeleteLists;
