import React, { useState } from "react";

const List = React.memo(
  ({
    id,
    title,
    completed,
    todoData,
    setTodoData,
    provided,
    snapshot,
    handleClick,
  }) => {
    console.log("List rendering");

    const [isEditing, setEditState] = useState(false);

    const handleEditSubmit = (id, e) => {
        e.preventDefault();

        setEditState(false)

    }

    const handleCompleteChange = (id) => {
      let newTodoData = todoData.map((data) => {
        if (data.id === id) {
          data.completed = !completed;
        }
        return data;
      });
      setTodoData(newTodoData);
    };


    if(isEditing){
        return(
            <div
            key={id}
            {...provided.draggableProps}
            ref={provided.innerRef}
            {...provided.dragHandleProps}
            className={`${
              snapshot.isDragging ? "bg-gray-300" : "bg-gray-100"
            } flex items-center justify-between w-full px-4 py-1 my-2 text-gray-600 bg-gray-100 border rounded`}
          >
            <div className="items-center">
                <form onSubmit={(e) => handleEditSubmit(id, e)} >
                    <input type="text" name="value" placeholder="수정할 내용을 입력하세요."/>
                </form>
            </div>
            <div className="items-center">
              <button
                className="text-sm mx-3 my-2 float-left text-gray-500"
                type="submit"
              >
                  완료
              </button>
              <button
                className="text-sm px-1 my-2 float-right bg-red-400 text-white rounded"
                onClick={() => {
                  handleClick(id);
                }}
              >
                X
              </button>
            </div>
          </div>            
        )
    }
    else{
        return (
            <div
              key={id}
              {...provided.draggableProps}
              ref={provided.innerRef}
              {...provided.dragHandleProps}
              className={`${
                snapshot.isDragging ? "bg-gray-300" : "bg-gray-100"
              } flex items-center justify-between w-full px-4 py-1 my-2 text-gray-600 bg-gray-100 border rounded`}
            >
              <div className="items-center">
                <input
                  className="mr-2"
                  type="checkbox"
                  onClick={() => handleCompleteChange(id)}
                  defaultChecked={completed}
                />
                <span className={completed ? "line-through" : undefined}>
                  {title}
                </span>
              </div>
              <div className="items-center">
                <button
                  className="text-sm mx-3 my-2 float-left text-gray-500"
                  onClick={() => {
                      setEditState(true);
                  }}
                >
                    수정
                </button>
                <button
                  className="text-sm px-1 my-2 float-right bg-red-400 text-white rounded"
                  onClick={() => {
                    handleClick(id);
                  }}
                >
                  X
                </button>
              </div>
            </div>
          );
    }

  }
);

export default List;
